function redirectToAnalysis(e){e&&(window.location.href=`analysis.html?ticker=${e}`)}document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("company-search"),n=document.getElementById("analyze-btn"),t=()=>{const n=e.value.trim().toUpperCase();n?window.location.href=`analysis.html?ticker=${n}`:alert("Veuillez entrer un nom ou un symbole boursier.")};n.addEventListener("click",t),e.addEventListener("keypress",e=>{"Enter"===e.key&&t()})});