const API_BASE="https://finanalyses.onrender.com/api";function createNewsCard(e){}async function fetchAndDisplayNews(){const e=document.getElementById("news-container");if(e){e.innerHTML="<p>Chargement des dernières actualités...</p>";try{const t=await fetch(`${API_BASE}/news`);if(!t.ok){const e=await t.json();throw new Error(e.detail||`Erreur HTTP: ${t.status}`)}const n=await t.json();console.log("Données reçues de l'API:",n),e.innerHTML="",n.articles&&n.articles.length>0?n.articles.forEach(t=>{e.innerHTML+=createNewsCard(t)}):e.innerHTML='<p class="text-gray-500">Aucune actualité trouvée pour le moment.</p>'}catch(t){console.error("Erreur lors de la récupération des actualités:",t),e.innerHTML=`<div class="text-red-600">Impossible de charger les actualités. Raison: ${t.message}</div>`}}}document.addEventListener("DOMContentLoaded",fetchAndDisplayNews);