const API_BASE="https://finanalyses.onrender.com/api";function createNewsCard(e){return`\n        <div class="bg-white p-4 rounded-lg shadow hover:shadow-md transition-shadow duration-200 flex gap-4">\n            <img src="${e.image_url}" alt="" class="w-24 h-24 object-cover rounded">\n            <div class="flex-1">\n                <a href="${e.url}" target="_blank" rel="noopener noreferrer" class="block">\n                    <h3 class="font-semibold text-gray-800 hover:text-blue-600">${e.title}</h3>\n                    <p class="text-sm text-gray-600 mt-2">${e.description}</p> \n                    <p class="text-xs text-gray-400 mt-3">Source: ${e.source}</p>\n                </a>\n            </div>\n        </div>\n    `}async function fetchAndDisplayNews(){const e=document.getElementById("news-container");if(e){e.innerHTML="<p>Chargement des dernières actualités...</p>";try{const t=await fetch(`${API_BASE}/news`);if(!t.ok){const e=await t.json();throw new Error(e.detail||`Erreur HTTP: ${t.status}`)}const n=await t.json();console.log("Données reçues de l'API:",n),e.innerHTML="",n.articles&&n.articles.length>0?n.articles.forEach(t=>{e.innerHTML+=createNewsCard(t)}):e.innerHTML='<p class="text-gray-500">Aucune actualité trouvée pour le moment.</p>'}catch(t){console.error("Erreur lors de la récupération des actualités:",t),e.innerHTML=`<div class="text-red-600">Impossible de charger les actualités. Raison: ${t.message}</div>`}}}document.addEventListener("DOMContentLoaded",fetchAndDisplayNews);