const API_BASE="https://finanalyses.onrender.com/api";function createNewsCard(e){const n=e.url||"#",t=e.title||"Titre non disponible",o=e.snippet||e.description||"Contenu non disponible",r=e.source||"Source inconnue",s=e.image_url;return`\n        <div class="bg-white p-4 rounded-lg shadow hover:shadow-md transition-shadow duration-200">\n            <a href="${n}" target="_blank" rel="noopener noreferrer" class="block">\n                ${s?`<img src="${s}" alt="" class="w-full h-32 object-cover rounded-md mb-4">`:""}\n                <h3 class="font-semibold text-gray-800 hover:text-blue-600">${t}</h3>\n                <p class="text-sm text-gray-600 mt-2">${o}</p>\n                <p class="text-xs text-gray-400 mt-3">Source: ${r}</p>\n            </a>\n        </div>\n    `}