const IS_LOCAL=window.location.hostname.includes("localhost")||window.location.hostname.includes("127.0.0.1"),API_BASE=IS_LOCAL?"http://localhost:8000/api":"https://finanalyses.onrender.com/api";let currentCompanyData=null;function createNewsCard(n){const e=n.url||"#",t=n.title||"Titre non disponible",o=n.snippet||n.description||"Contenu non disponible",a=n.source||"Source inconnue",r=n.image_url;return`\n        <div class="bg-white p-4 rounded-lg shadow hover:shadow-md transition-shadow duration-200">\n            <a href="${e}" target="_blank" rel="noopener noreferrer" class="block">\n                ${r?`<img src="${r}" alt="" class="w-full h-32 object-cover rounded-md mb-4">`:""}\n                <h3 class="font-semibold text-gray-800 hover:text-blue-600">${t}</h3>\n                <p class="text-sm text-gray-600 mt-2">${o}</p>\n                <p class="text-xs text-gray-400 mt-3">Source: ${a}</p>\n            </a>\n            \x3c!-- Dans la barre de navigation --\x3e\n<a href="chat.html" class="text-gray-600 hover:text-blue-600 px-3 py-2 rounded-md font-medium">Chat AI</a>\n        </div>\n    `}